mapper.select:
select
qa.goods_id,qa.questions_id,qa.question,qa.answer,help_qa.submit_date,help_qa.answers_date,
count(help_qa.user_id) as help_num
from question_and_answer as qa left joint question_sannkou as help_qa 
on help_qa.goods_id=qa.goods_id help_qa.questions_id=qa.questions_id


		and qa.goods_id=#{goodsId}


group by qa.goods_id,qa.questions_id,qa.question,qa.answer,help_qa.submit_date,help_qa.answers_date
order by ${orderByColumn} ${ascOrDesc}

	limit #{start},#{limit}

SELECT count(questions_id) as help_num
FROM question_sannkou 
where questions_id = #{questionsId,jdbcType=BIGINT}
