mapper.select:
select rs.goods_id,rs.review_id,rs.submit_date,r.review_user_id,r.star,r.review_title,r.review_detail,r.image_url, count(rs.review_id) as help_num from review_sannkou as rs , review as r
and rs.goods_id=#{goodsId} and rs.goods_id=r.goods_id and rs.review_id =r.review_id
group by rs.goods_id,rs.review_id,rs.submit_date,r.review_user_id,r.star,r.review_title,r.review_detail,r.image_url order by ${orderByColumn} ${ascOrDesc}
limit #{start},#{limit}

select r.goods_id,r.review_id,r.submit_date,r.review_user_id,r.star,r.review_title,r.review_detail,r.image_url, count(rs.review_id) as help_num from review as r left join review_sannkou as rs on rs.goods_id=r.goods_id and rs.review_id =r.review_id
and r.goods_id=#{goodsId}
group by r.goods_id,r.review_id,r.submit_date,r.review_user_id,r.star,r.review_title,r.review_detail,r.image_url order by help_num Desc
limit #{start},#{limit}

select r.goods_id,r.review_id,r.submit_date,r.review_user_id,r.star,r.review_title,r.review_detail,r.image_url, count(rs.review_id) as help_num from review as r left join review_sannkou as rs on rs.goods_id=r.goods_id and rs.review_id =r.review_id
and r.goods_id=#{goodsId}
group by r.goods_id,r.review_id,r.submit_date,r.review_user_id,r.star,r.review_title,r.review_detail,r.image_url order by help_num Desc
